import React from 'react';
import { useForm, ValidationError } from '@formspree/react';
import "./contactForm.css"

import MyButton from "./MyButton"

function ContactForm() {
    const [state, handleSubmit] = useForm("maykznvk");
    if (state.succeeded) {
        return <p className={"submittedText"}>Thanks for your message!</p>;
    }
    return (
        <form onSubmit={handleSubmit} className={"contactForm"}>
            <small>Use this space to ask me about my work, commissions, or to just say hi!</small>
            <div className={"formSection"}>
                <input
                    id="name"
                    type="name"
                    name="name"
                    placeholder={"Your Name"}
                />
                <ValidationError
                    prefix="Name"
                    field="name"
                    errors={state.errors}
                />
            </div>
            <div className={"formSection"}>
                <input
                    id="email"
                    type="email"
                    name="email"
                    placeholder={"Your Email"}
                />
                <ValidationError
                    prefix="Email"
                    field="email"
                    errors={state.errors}
                />
            </div>
            <div className={"messageArea formSection"}>
                <textarea
                    id="message"
                    name="message"
                    placeholder={"Your Message"}
                />
                <ValidationError
                    prefix="Message"
                    field="message"
                    errors={state.errors}
                />
            </div>
            <MyButton type="submit" disabled={state.submitting} className={"sendBtn"} btnText={`send`} btnType={"send"}></MyButton>
        </form>
    );
}

export default ContactForm;
