import React from 'react';
import "./art.css"
import MyButton from "./MyButton";

function ContactForm(props) {
    const imageSrc = props.imageRoute
    const imgName = props.imageName
    function display(imageId){
        document.getElementById(imageId).style.visibility="visible"
    }

    function remove(imageId){
        document.getElementById(imageId).style.visibility="hidden"
    }

    return (
        <div>
            <div className={"artwork"}>
                <img src={imageSrc} onClick={() => display(imgName)}/>
            </div>
            <div className={"popUp"}>
                <div className={"popUpContent"} id={`${props.imageName}`}>
                    <img src={imageSrc}/>
                    <h3>{imgName}</h3>
                    <MyButton btnText={"Close"} onClick={() => remove(imgName)}></MyButton>
                </div>
            </div>
        </div>
    );
}

export default ContactForm;
